var fs = require('fs');

var get_creds = function(file){
    file = process.env['HOME'] + "/creds/" + file;
    var data = fs.readFileSync(file, "utf8");
    var lines = data.split("\n");
    retval = {};
    for(l in lines){
        if(lines[l] == "") { continue; }
        b = /^(.+?):(.+)/mi(lines[l]);
        retval[b[1]] = b[2];
    }
    return retval;
};

exports.get_creds = get_creds;
